<?phpnamespace Album\Controller;use Zend\Mvc\Controller\AbstractActionController;use Zend\View\Model\ViewModel;use Album\Model\AlbumTable;/** * AlbumController * * @author * * @version * */class AlbumController extends AbstractActionController{    protected $albumTable;        public function getAlbumTable()    {        if (!$this->albumTable) {            $sm = $this->getServiceLocator();            $this->albumTable = $sm->get('Album\Model\AlbumTable');        }        return $this->albumTable;    }        /**     * The default action - show the home page     */    public function indexAction ()    {        return new ViewModel(array(            'albums' => $this->getAlbumTable()->fetchAll(),        ));    }        public function addAction()    {            }        public function editAction()    {            }        public function deleteAction()    {            }}