<?phpnamespace Album\Model;/** * * @author diego *         */class Album{    public $id;    public $artist;    public $title;    /**     * Necesario para implementar Zend\Db\TableGateway     * @param array $data     */    public function exchangeArray ($data)    {        $this->id     = (isset($data['id'])) ? $data['id'] : null;        $this->artist = (isset($data['artist'])) ? $data['artist'] : null;        $this->title  = (isset($data['title'])) ? $data['title'] : null;    }}